mapper.select:
select u.id "id", u.name "name", u.ch_name "chName", u.role "roleId", u.dept_id "deptId", d.name "deptName", r.name "roleName" from sys_user u left join sys_dept d on d.id=u.dept_id left join sys_role r on u.role=r.id where u.status='0'
and u.name = #{name}
and u.PASSWORD = #{passWord}

select d.id "id", d.name "name" , d.scope "scope" , d.remark "remark"from sys_dept d where 1=1

    and  d.name like #{name}


    and  d.id=#{id}

mapper.update:
update sys_dept set
name=#{name},
scope=#{scope},
remark=#{remark}
where
id=#{id}

mapper.select:
select u.name "name", u.id "id", u.sex "sex", u.status "status", u.ch_name "chName", u.home "home", u.address "address", u.idcard "idcard", d.name "deptName", r.name "roleName", d.id "deptId" from sys_user u left join sys_dept d on d.id=u.dept_id left join sys_role r on r.id=u.role where 1=1
and u.id=#{id}
and u.role=#{roleId}
and u.dept_id=#{deptId}
and u.ch_name like #{name}
and u.status=#{status}

mapper.update:
update sys_user SET
status=1
where id=#{id}

mapper.select:
select w.id "id", date_format(w.date, '%Y-%c-%d' ) "date", w.local "local", w.info "info", w.type "type", w.remark "remark" from emp_work w
where 1=1
and w.user_id=#{userId}
AND w.type=#{type}
